import React,{useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css'

function Form() {
  const NewTask = ({addTask})=>{    
    const [value,newWork] = useState("");
  }

  return (
    <form>
    <div className="form-group" >
      <label >Task</label>
      <input type="text" className="form-control"  placeholder="Enter Task" required
       name="name" value={value} onChange={(e)=>newWork(e.target.value)}
      />

    </div>
    <div className="form-group">
    <label >Task Type</label>
        <select className="custom-select" required>
        <option disabled> select menu</option>
        <option value="Billable">Billable</option>
        <option value="NonBillable">Non-Billable</option>
        <option value="idle">idle</option>
        </select>
    </div>
    <div className="form-group">
      <label >Description</label>
      <input type="text" className="form-control"  placeholder="Description about task" required/>
    </div>

    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
  );
}

export default Form;
